import React, { Component, useEffect, useState } from "react";
import API from "../utils/API";
import Container from "../components/Container";
import SearchResults from "../components/SearchResults";
import SearchForm from "../components/SearchForm";

class Search extends Component {

  state = {
    search: "",
    employee: [],
    results: [],
  };

  componentDidMount() {
    let storedEmployees = JSON.parse(localStorage.getItem("Employees"));
    if (storedEmployees != null) {
        this.setState({ results: storedEmployees })
    }
    else{
        API.getEmployees()
        .then(res => {
        this.setState({ results: res.data.results })
        localStorage.setItem("Employees", JSON.stringify(res.data.results))
        })
        .catch(err => console.log(err));}
  }

  handleInputChange = event => {
    const value = event.target.value;
    let storedEmployees = JSON.parse(localStorage.getItem("Employees"));
    let filteredEmployees = storedEmployees.filter(letter => letter.name.first.includes(value))
    this.setState({ results: filteredEmployees });
  };

  sortEmployees = (a,b) => {
    alert("test")
    let storedEmployees = JSON.parse(localStorage.getItem("Employees"));
    console.log(storedEmployees)

    const sorted = storedEmployees.sort((a, b) => b[storedEmployees.name] - a[storedEmployees.name.first]);
    console.log(sorted);
    this.setState({ results: sorted });
    // const nameA = a.name.first.toUpperCase();
    // const nameB = b.name.first.toUpperCase();
    // let comparison = 0;
    // if (nameA > nameB) {
    // comparison = 1;
    // } else if (nameA < nameB) {
    // comparison = -1; 
    // }
    // console.log(comparison)
    // return comparison;
  }

// toggleSortFirstName = () =>{
//     let newEmployeeList = JSON.parse(localStorage.getItem("Employees"));
//     console.log(newEmployeeList)
//     this.setState({
//         employeeListChanges: newEmployeeList.sort((a, b) => a.name.first > b.name.first)
//     })
//     localStorage.setItem("Employees", JSON.stringify(newEmployeeList))
// }

  render() {
    return (
      <div>
        <Container style={{ minHeight: "80%" }}>
          <h1 className="text-center">Employee Directory!</h1>

          <SearchForm
            handleInputChange={this.handleInputChange}
            employee={this.state.employee}
          />
            <tr>
                <th>Photo</th>
                <th onClick={event => {event.preventDefault(); this.sortEmployees('first');}}>
                First Name
                </th>
                <th>Last Name</th>
                <th>Gender</th>
                <th>Email</th>
            </tr>
          <SearchResults results={this.state.results} />
        </Container>
      </div>
    );
  }
}

export default Search;
